raiz.functions.list._isjson = function(string){    
    if(string == undefined || string == null || string.replace(/ /g,'') == '' ) return false;
    if (/^[\],:{}\s]*$/.test(string.replace(/\\["\\\/bfnrtu]/g, '@').
    replace(/"[^"\\\n\r]*"|true|false|null|-?\d+(?:\.\d*)?(?:[eE][+\-]?\d+)?/g, ']').
    replace(/(?:^|:|,)(?:\s*\[)+/g, ''))) {
      return true;
    }else{
      return false;
    }
}


